    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        // TODO add your handling code here:
        if((!(this.jRadioButtonVentaProducto.isSelected())) && (!this.jRadioButtonVentaTOtal.isSelected())){
            JOptionPane.showMessageDialog(null, "Escoja uno de las dos opciones disponibles para graficar");
        }
        else{
            Graficador grafico = new Graficador("Ventas Mensuales", "Ventas Mensuales");
            grafico.pack();
            grafico.setVisible(true);
                }
    }//GEN-LAST:event_jButton1ActionPerformed

